import React from 'react';
import './App.css';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import Columns from 'react-columns';
import FormDialog from './FormDialog';
import green from '@material-ui/core/colors/green'
import lightgreen from '@material-ui/core/colors/lightGreen'
import lime from '@material-ui/core/colors/lime'
import { ThemeProvider } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core/styles';




//TODO: bebas neue - font for title


// colors
const bestClass = green; // #F44336
const recClass = lightgreen[400]; // #E040FB
const lessRecClass = lime[400];

const bestClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        background: lessRecClass,
      },
    },
  },
});

const recClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        background: lessRecClass,
      },
    },
  },
});

const lessRecClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        background: lessRecClass,
      },
    },
  },
});


function GridPart(){
  // const classes = useStyles();

  return(
    <Grid
      container
      spacing = {1 }
      direction="column"
      justify="flex-start"
      alignItems="center"
      >

      <Grid item xs={12}>
        <ThemeProvider theme={bestClassTheme}>
          <Paper className = {classes.otherClass} >Kid's Class</Paper>
        </ThemeProvider>
      </Grid>

      <Grid item xs={12} >
        <Paper className = {classes.otherClass}>Adult Class</Paper>
      </Grid>

      <Grid item xs={12}>
        <Paper className = {classes.otherClass}>Adult Class</Paper>
      </Grid>

      <Grid item xs={12}>
        <Paper className = {classes.otherClass}>Adult Class</Paper>
      </Grid>

      <Grid item xs={12}>
        <Paper className = {classes.otherClass}>Adult Class</Paper>
      </Grid>

    </Grid> 
  );
}

function Component(){

    return (
    <Columns columns="4">
      <div style = {{padding: 20}}>
      <p>Monday</p>
       <GridPart/>
       </div>
       <div style = {{padding: 20}}>
       <p>Tuesday</p>

       <GridPart/>
       </div>
       <div style = {{padding: 20}}>
       <p>Wednesday</p>

       <GridPart/>
       </div>
       <div style = {{padding: 20}}>
       <p>Thursday</p>

       <GridPart/>
       </div>

    </Columns>
  );
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h2>
          TKC Free Trial Class Sign-up
        </h2>
        <p className="textBlurb">
          We always work around our families’ schedules. If any of these recommended times don’t work for you OR for holiday hours, please call (630) 708-3132.
        </p>
        <Component/>
        <FormDialog/>
      </header>
    </div>
  );
}

export default App;

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    backgroundColor: green
  },

  otherClass: {
    padding: theme.spacing(2),
    textAlign: 'center',
    // color: theme.palette.text.secondary,
    height: 70,
    width: 200,
    fontSize: 10
  }
}));
