import React from 'react';
import './App.css';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Columns from 'react-columns';
import FormDialog from './FormDialog';
import green from '@material-ui/core/colors/green'
import lightgreen from '@material-ui/core/colors/lightGreen'
import lime from '@material-ui/core/colors/lime'
import { ThemeProvider, makeStyles } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core/styles';




//TODO: bebas neue - font for title


// colors
const bestClass = green[500]; // #F44336
const recClass = lightgreen[400]; // #E040FB
const lessRecClass = lime[400];

const classTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: bestClass,
      },
    },
  },
});

const classTheme2 = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: recClass,
      },
    },
  },
});

const classTheme3 = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: lessRecClass,
      },
    },
  },
});


function PaperPart(props){
  const classes = useStyles();

  return(
        <Paper className = {classes.otherClass}>{props.className}</Paper>
  );
}

function GridPart(){
  const kidClass = "Kid's Class";
  const teenClass = "Teen Class";
  const adultClass = "Adult Class";
  const familyClass = "Family Class";


  return(
    <Grid
      container
      spacing = {1}
      direction="column"
      justify="flex-start"
      alignItems="center"
      >

      <ThemeProvider theme={classTheme}>
        <Grid item xs={12}>
          <PaperPart className = {kidClass}/>
        </Grid>
        <Grid item xs={12}>
          <ThemeProvider theme={classTheme2}>
            <PaperPart className = {teenClass}/>
          </ThemeProvider>
        </Grid>
        <Grid item xs={12}>
          <ThemeProvider theme={classTheme3}>
            <PaperPart className = {adultClass}/>
          </ThemeProvider>
        </Grid>
        <Grid item xs={12}>
          <ThemeProvider theme={classTheme2}>
            <PaperPart className = {adultClass}/>
          </ThemeProvider>
        </Grid>
        <Grid item xs={12}>
          <ThemeProvider theme={classTheme2}>
            <PaperPart className = {familyClass}/>
          </ThemeProvider>
        </Grid>
      </ThemeProvider>
    </Grid> 
  );
}

function Component(){

    return (
    <Columns columns="4">
      <div style = {{padding: 20}}>
      <p>Monday</p>
       <GridPart/>
       </div>
       <div style = {{padding: 20}}>
       <p>Tuesday</p>

       <GridPart/>
       </div>
       <div style = {{padding: 20}}>
       <p>Wednesday</p>

       <GridPart/>
       </div>
       <div style = {{padding: 20}}>
       <p>Thursday</p>

       <GridPart/>
       </div>

    </Columns>
  );
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h2>
          TKC Free Trial Class Sign-up
        </h2>
        <p className="textBlurb">
          We always work around our families’ schedules. If any of these recommended times don’t work for you OR for holiday hours, please call (630) 708-3132.
        </p>
        <Component/>
        <FormDialog/>
      </header>
    </div>
  );
}

export default App;

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },

  otherClass: {
    padding: theme.spacing(2),
    textAlign: 'center',
    // color: theme.palette.text.secondary,
    height: 70,
    width: 200,
    fontSize: 10,
  }
}));
