});

const lessRecClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: lessRecClass,
      },
    },
  },
});

const PaperPart = ({classTheme}) => {
  const classes = useStyles();

  return(
      <ThemeProvider theme={this.props.classTheme}>
        <Paper className = {classes.otherClass}>Kid's Class</Paper>
      </ThemeProvider>
  );
}

