import React from 'react';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box'
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { ThemeProvider, makeStyles } from '@material-ui/styles';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },

  otherClass: {
    padding: theme.spacing(2),
    margin: theme.spacing(.5),
    textAlign: 'left',
    [theme.breakpoints.up('xs')]: {
      width: 50,
      fontSize: 8,
    },
    [theme.breakpoints.up('sm')]: {
      width: 90,
      fontSize: 10,

    },
    [theme.breakpoints.up('md')]: {
      width: 120,
      fontSize: 12,

    },
    [theme.breakpoints.up('lg')]: {
      width: 150,
      fontSize: 14,

    },
  }
}));

export default function ClassButton(props){
  const classes = useStyles();
  const [open, setOpen] = React.useState(true);
  const theme = useStyles();

  // const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));

  function handleClickOpen() {
    setOpen(true);
  }

  function handleClose() {
    setOpen(false);
  }

  function handleRegisterClose() {
    // TODO: check if inputs entered
    // setOpen(false);
    // open another dialog box of confirmation 
  }

  return(
    <div>
      <Button onClick={handleClickOpen}>
        <Paper className = {classes.otherClass}>
          <Box fontWeight = "fontWeightBold" paddingBottom = {1} > {props.classTime}</Box>
          {props.className}
        </Paper>
      </Button>
      <Dialog onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Register</DialogTitle>
        <DialogContent>
          <DialogContentText>
            You have chosen to have the free trial class on ________ . Enter the information below to complete registration. 
          </DialogContentText>
          <TextField
            // autoFocus = "false"
            margin="normal"
            id="email"
            label="Email Address"
            type="email"
            required
            fullWidth
          />
          <TextField
            // autoFocus="false"
            margin="normal"
            id="name"
            label="Name(s) of participant(s)"
            type="text"
            required
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleRegisterClose} color="primary">
            Register
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
