import React from 'react';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box'
import green from '@material-ui/core/colors/green'
import lightgreen from '@material-ui/core/colors/lightGreen'
import lime from '@material-ui/core/colors/lime'
import { ThemeProvider, makeStyles } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core/styles';
import { borders } from '@material-ui/system';

// const basicClass = white[500];
const bestClass = green[500]; // #F44336
const recClass = lightgreen[400]; // #E040FB
const lessRecClass = lime[400];

const classTheme = createMuiTheme({

  overrides: {
    // Style sheet name ⚛️

    MuiButton: {
      text: {
        padding: 0,
      },
      root: {
        border: 1,
        borderColor: lessRecClass,
      }
    },
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: '#FFFFFF',
      },
    },
  },
});

const bestClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiButton: {
      text: {
        padding: 0,
      },
    },
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: bestClass,
      },
    },
  },
});

const recClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiButton: {
      text: {
        padding: 0,
      },
    },
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: recClass,
      },
    },
  },
});

const lessRecClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiButton: {
      text: {
        padding: 0,
      },
    },
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: lessRecClass,
      },
    },
  },
});

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },

  otherClass: {
    padding: theme.spacing(2),
    margin: theme.spacing(.5),
    textAlign: 'left',
    [theme.breakpoints.up('xs')]: {
      width: 50,
      fontSize: 8,
    },
    [theme.breakpoints.up('sm')]: {
      width: 90,
      fontSize: 10,

    },
    [theme.breakpoints.up('md')]: {
      width: 120,
      fontSize: 12,

    },
    [theme.breakpoints.up('lg')]: {
      width: 150,
      fontSize: 14,

    },
  }
}));

function PaperPart(props){
  const classes = useStyles();

  return(
    <Button >
      <Paper className = {classes.otherClass}>
        <Box fontWeight = "fontWeightBold" paddingBottom = {1} > {props.classTime}</Box>
        {props.className}
      </Paper>
    </Button>
  );
}


export default function GridPart(){
  const kidClass = "Kids Taekwondo Class";
  const basicClass = "Basic Class";
  const advancedClass = "Advanced/ Black Belt Class";
  const familyBBClass = "Family Class/ Black Belt Class";
  const familyClass = "Family Class";

  const kidClassTime = "3:50 - 4:20pm";
  const basicClassTime = "4:20 - 5:00pm";
  const advancedClassTime = "5:10 - 5:50pm";
  const familyBBClassTime = "6:10 - 6:50pm";
  const familyClassTime = "7:10 - :50pm";

  return(
    <Grid
      container
      spacing = {1}
      direction="column"
      justify="flex-start"
      alignItems="center"
      >

      <ThemeProvider theme={classTheme}>
          <ThemeProvider theme={bestClassTheme}>
            <PaperPart className = {kidClass} classTime = {kidClassTime} />
          </ThemeProvider>
          <ThemeProvider theme={bestClassTheme}>
            <PaperPart className = {basicClass} classTime = {basicClassTime}/>
          </ThemeProvider>
            <PaperPart className = {advancedClass} classTime = {advancedClassTime}/>
          <ThemeProvider theme={recClassTheme}>
            <PaperPart className = {familyBBClass} classTime = {familyBBClassTime}/>
          </ThemeProvider>
          <ThemeProvider theme={lessRecClassTheme}>
            <PaperPart className = {familyClass} classTime = {familyClassTime}/>
          </ThemeProvider>
      </ThemeProvider>
    </Grid> 
  );
}
