import React from 'react';
import './App.css';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';

import Box from '@material-ui/core/Box'
import Columns from 'react-columns';
import FormDialog from './FormDialog';
import green from '@material-ui/core/colors/green'
import lightgreen from '@material-ui/core/colors/lightGreen'
import lime from '@material-ui/core/colors/lime'
// import white from '@material-ui/core/colors/white'
import { ThemeProvider, makeStyles } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core/styles';
import { borders } from '@material-ui/system';


//TODO: bebas neue - font for title

// colors
// const basicClass = white[500];
const bestClass = green[500]; // #F44336
const recClass = lightgreen[400]; // #E040FB
const lessRecClass = lime[400];

const classTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiGrid: {
      root: {
        border: 1,
        borderColor: bestClass,
      }
    },
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: '#FFFFFF',
        border: 1,
        borderColor: bestClass,
        // border: 1,
        // borderColor: bestClass
      },
    },
  },
});

const bestClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: bestClass,
      },
    },
  },
});

const recClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: recClass,
      },
    },
  },
});

const lessRecClassTheme = createMuiTheme({
  overrides: {
    // Style sheet name ⚛️
    MuiPaper: {
      // Name of the rule
      root: {
        // Some CSS
        backgroundColor: lessRecClass,
      },
    },
  },
});





// function PaperPart(props){
//   const classes = useStyles();

//   return(
//     <Paper className = {classes.otherClass}>
//       <Box fontWeight = "fontWeightBold" fontSize = {12} paddingBottom = {1}> {props.classTime}</Box>
//       {props.className}
//     </Paper>
//   );
// }


function PaperPart(props){
  const classes = useStyles();

  return(
    <Button className = {classes.otherClass}>
      <Box fontWeight = "fontWeightBold" fontSize = {12} paddingBottom = {1}> {props.classTime}</Box>
      {props.className}
    </Button>
  );
}

function GridPart(){
  const kidClass = "Kid's Taekwondo Class";
  const basicClass = "Basic Class";
  const advancedClass = "Advanced/Black Belt Class";
  const familyBBClass = "Family Class/Black Belt Class";
  const familyClass = "Family Class";

  const kidClassTime = "3:50 - 4:20pm";
  const basicClassTime = "4:20 - 5:00pm";
  const advancedClassTime = "5:10 - 5:50pm";
  const familyBBClassTime = "6:10 - 6:50pm";
  const familyClassTime = "7:10 - :50pm";

  return(
    <Grid
      container
      spacing = {1}
      direction="column"
      justify="flex-start"
      alignItems="center"
      >

      <ThemeProvider theme={classTheme}>
        <Grid item xs={12} >
          <ThemeProvider theme={bestClassTheme}>
            <PaperPart className = {kidClass} classTime = {kidClassTime}/>
          </ThemeProvider>
        </Grid>
        <Grid item xs={12} >
          <ThemeProvider theme={bestClassTheme}>
            <PaperPart className = {basicClass} classTime = {basicClassTime}/>
          </ThemeProvider>
        </Grid>
        <Grid item xs={12}>
            <PaperPart className = {advancedClass} classTime = {advancedClassTime}/>
        </Grid>
        <Grid item xs={12} >
          <ThemeProvider theme={recClassTheme}>
            <PaperPart className = {familyBBClass} classTime = {familyBBClassTime}/>
          </ThemeProvider>
        </Grid>
        <Grid item xs={12} onClick={FormDialog}>
          <ThemeProvider theme={lessRecClassTheme}>
            <PaperPart className = {familyClass} classTime = {familyClassTime}/>
          </ThemeProvider>
        </Grid>
      </ThemeProvider>
    </Grid> 
  );
}

function Component(){

    return (
    <Columns columns="4">
      <div style = {{paddingBottom: 20}}>
      <p>Monday</p>
       <GridPart/>
       </div>
       <div style = {{paddingBottom: 20}}>
       <p>Tuesday</p>

       <GridPart/>
       </div>
       <div style = {{paddingBottom: 20}}>
       <p>Wednesday</p>

       <GridPart/>
       </div>
       <div style = {{paddingBottom: 20}}>
       <p>Thursday</p>

       <GridPart/>
       </div>

    </Columns>
  );
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        {/* <h2> */}
          TKC Free Trial Class Sign-up
        {/* </h2> */}
        <div className = "textGen">
          <p className="textBlurb">
            We always work around our families’ schedules. If any of these recommended times don’t work for you OR for holiday hours, please call (630) 708-3132.
          </p>
          <Component/>
        </div>
      </header>
    </div>
  );
}

export default App;

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },

  otherClass: {
    padding: theme.spacing(2),
    textAlign: 'left',
    // color: theme.palette.text.secondary,
    height: 70,
    width: 200,
    fontSize: 16,
  }
}));
